add_action( 'wp_head', function () { ?>
<script>

	(function ($) {
    "use strict";

		//  var $wn = $(window),
        // $doc = $(document);

    $(function () {
        /* ----------------------------------------------------------- */
        /* Back to top
        /* ----------------------------------------------------------- */
        $(window).on('scroll', function () {
            if ( $(window).scrollTop() > $(window).height() ) {
                $('.BackTo').fadeIn('slow');
            } else {
                $('.BackTo').fadeOut('slow');
            }
        });

        $('body, html').on('click', '.BackTo', function (e) {
            e.preventDefault();

            $('html, body').animate({
                scrollTop: 0
            }, 800);
        });
    });

}(jQuery));

</script>
<?php } );


// HTML shortcode
